import React from 'react';
import {
  BarChart, Bar, XAxis, YAxis, CartesianGrid, Tooltip, Legend,
} from 'recharts';

const calculateAverageRating = (players) => {
  const totalRating = players.reduce((acc, player) => acc + player.average_rating, 0);
  return players.length ? totalRating / players.length : 0;
};

const ComparisonChart = ({ teams }) => {
  const categories = ['Attack', 'Midfield', 'Defense'];
  const data = categories.map(category => {
    const categoryData = {
      name: category,
    };

    teams.forEach(team => {
      const players = team.players.filter(player => {
        if (category === 'Defense') {
          return player.broad_position === 'Defender' || player.broad_position === 'Goalkeeper';
        }
        return player.broad_position === category;
      });

      categoryData[team.name] = calculateAverageRating(players);
    });

    return categoryData;
  });

  const colors = ['#8884d8', '#82ca9d', '#ffc658'];

  return (
    <BarChart
      width={600}
      height={300}
      data={data}
      margin={{
        top: 20, right: 30, left: 20, bottom: 5,
      }}
    >
      <CartesianGrid strokeDasharray="3 3" />
      <XAxis dataKey="name" />
      <YAxis />
      <Tooltip />
      <Legend />
      {teams.map((team, index) => (
        <Bar key={team.name} dataKey={team.name} fill={colors[index]} barSize={20} />
      ))}
    </BarChart>
  );
};

export default ComparisonChart;
