import { TypeAnimation } from 'react-type-animation';


import { Canvas, useFrame } from '@react-three/fiber';
import React, { useRef , useState} from 'react';
import './HomePage.css';

const SoccerBall = () => {
  const mesh = useRef();
  const [velocity, setVelocity] = useState(0);
  const [bounceHeight, setBounceHeight] = useState(5);

  useFrame(() => {
    if (mesh.current) {
      // Gravity effect
      setVelocity(prev => prev - 0.1);

      // Update the position
      mesh.current.position.y += velocity;

      // Bounce effect
      if (mesh.current.position.y <= 1) {
        mesh.current.position.y = 1;
        setVelocity(-velocity * 0.7);  // Decrease velocity for dampening effect
      }

      // Stop bouncing when velocity is too small
      if (Math.abs(velocity) < 0.05) {
        setVelocity(0);
        mesh.current.position.y = 1;
      }
    }
  });

  return (
    <mesh position={[0, bounceHeight, 0]} ref={mesh}>
      <sphereGeometry args={[1, 32, 32]} />
      <meshStandardMaterial color="white" />
    </mesh>
  );
};

const Scene = () => {
  return (
    <Canvas>
      <ambientLight />
      <pointLight position={[10, 10, 10]} />
      <SoccerBall />
      <mesh position={[0, 0, 0]} rotation={[-Math.PI / 2, 0, 0]}>
        <planeBufferGeometry args={[100, 100]} />
        <meshStandardMaterial color="green" />
      </mesh>
    </Canvas>
  );
};


const TypingComponent = () => {
    return (
      <div style={{ fontSize: '1.5em' }}>
        <span>This app is built by a collaborative team of:</span>
        <br />
        <TypeAnimation
          sequence={[
            'Data Scientists',
            1000, // wait 1s before typing the next text
            'Software Engineers',
            1000,
            'Quant Researchers',
            1000
          ]}
          wrapper="span"
          speed={50}
          repeat={Infinity}
          style={{ display: 'inline-block' }}
        />
      </div>
    );
  };

const HomePage = () => {
    return (
      <div className="home-page">
        <h1>Welcome to Our Quant Sport</h1>
        <TypingComponent />
        <div className="animation-container">
        <Scene/>
        </div>
      </div>
    );
  };

export default HomePage;
