import React from 'react';
import {
  BarChart, Bar, XAxis, YAxis, CartesianGrid, Tooltip, Legend, LabelList
} from 'recharts';

const calculateAverageRating = (players) => {
  const totalRating = players.reduce((acc, player) => acc + player.average_rating, 0);
  return players.length ? totalRating / players.length : 0;
};

const ComparisonChart = ({ teams }) => {
  const data = teams.map(team => {
    const forwards = team.players.filter(player => player.broad_position === 'Attacker');
    const midfielders = team.players.filter(player => player.broad_position === 'Midfielder');
    const defenders = team.players.filter(player => player.broad_position === 'Defender' || player.broad_position === 'Goalkeeper');

    return {
      name: team.name,
      Attack: calculateAverageRating(forwards),
      Midfield: calculateAverageRating(midfielders),
      Defense: calculateAverageRating(defenders),
    };
  });

  return (
    <BarChart
      width={600}
      height={400}
      data={data}
      margin={{
        top: 20, right: 30, left: 20, bottom: 5,
      }}
    >
      <CartesianGrid strokeDasharray="3 3" />
      <XAxis dataKey="name" />
      <YAxis />
      <Tooltip />
      <Legend />
      <Bar dataKey="Attack" fill="#8884d8" barSize={20}>
        <LabelList dataKey="Attack" position="top" />
      </Bar>
      <Bar dataKey="Midfield" fill="#82ca9d" barSize={20}>
        <LabelList dataKey="Midfield" position="top" />
      </Bar>
      <Bar dataKey="Defense" fill="#ffc658" barSize={20}>
        <LabelList dataKey="Defense" position="top" />
      </Bar>
    </BarChart>
  );
};

export default ComparisonChart;
