import { TypeAnimation } from 'react-type-animation';


import { Canvas, useFrame } from '@react-three/fiber';
import { Physics } from '@react-three/cannon';
import { OrbitControls, Stars } from '@react-three/drei';
import React, { useRef , useState} from 'react';
import './HomePage.css';
import { useSphere } from '@react-three/cannon';

const SoccerBall = (props) => {
  const [ref] = useSphere(() => ({ mass: 1, position: [0, 5, 0], ...props }));

  return (
    <mesh ref={ref} castShadow>
      <sphereBufferGeometry args={[1, 32, 32]} />
      <meshStandardMaterial color="white" />
    </mesh>
  );
}

const MainScene = () => {
  return (
    <Canvas shadowMap>
      <ambientLight intensity={0.5} />
      <pointLight position={[10, 10, 10]} />
      <Physics gravity={[0, -10, 0]}>
        <SoccerBall />
        <mesh receiveShadow position={[0, 0, 0]} rotation={[-Math.PI / 2, 0, 0]}>
          <planeBufferGeometry attach="geometry" args={[100, 100]} />
          <shadowMaterial attach="material" opacity={0.5} />
        </mesh>
      </Physics>
      <OrbitControls />
      <Stars />
    </Canvas>
  );
};


const TypingComponent = () => {
    return (
      <div style={{ fontSize: '1.5em' }}>
        <span>This app is built by a collaborative team of:</span>
        <br />
        <TypeAnimation
          sequence={[
            'Data Scientists',
            1000, // wait 1s before typing the next text
            'Software Engineers',
            1000,
            'Quant Researchers',
            1000
          ]}
          wrapper="span"
          speed={50}
          repeat={Infinity}
          style={{ display: 'inline-block' }}
        />
      </div>
    );
  };

const HomePage = () => {
    return (
      <div className="home-page">
        <h1>Welcome to Our Futuristic Hedge Fund</h1>
        <TypingComponent />
        <div className="animation-container">
          <MainScene />
        </div>
      </div>
    );
  };

  export default HomePage;
